  /** Abscissas for the current method. */
  List<double> _abscissas;

  /** Weights for the current method. */
  List<double> _weights;

  
  GaussLegendreIntegrator(final int n, {double relativeAccuracy,
                     double absoluteAccuracy,
                     int minimalIterationCount,
                     int maximalIterationCount}) {
    if (relativeAccuracy == null) 
      relativeAccuracy = BaseAbstractUnivariateIntegrator.DEFAULT_RELATIVE_ACCURACY;
    if (absoluteAccuracy == null) 
      absoluteAccuracy = BaseAbstractUnivariateIntegrator.DEFAULT_ABSOLUTE_ACCURACY;
    if (minimalIterationCount == null) 
      minimalIterationCount = BaseAbstractUnivariateIntegrator.DEFAULT_MIN_ITERATIONS_COUNT;
    
    switch (n) {
      case 4: 
        _abscissas = _ABSCISSAS_4;
        _weights = _WEIGHTS_4;
        break;
      case 64:  
        _abscissas = new List.from(_ABSCISSAS_64);
        _abscissas.addAll(_ABSCISSAS_64.map((e) => -e));
        _weights = new List.from(_WEIGHTS_64);
        _weights.addAll(_WEIGHTS_64);
        break;
      case 1024:
        _abscissas = new List.from(_ABSCISSAS_1024);
        _abscissas.addAll(_ABSCISSAS_1024.map((e) => -e));
        _weights = new List.from(_WEIGHTS_1024);
        _weights.addAll(_WEIGHTS_1024);
        break;       
      default:
        throw new Exception("Only order 4, 64, 1024 is supported!");
    } 
    
    
    super.initialize(
        relativeAccuracy: relativeAccuracy, 
        absoluteAccuracy: absoluteAccuracy, 
        minimalIterationCount: minimalIterationCount, 
        maximalIterationCount: maximalIterationCount); 
    
  }
  
  double doIntegrate() {
    
    // compute first estimate with a single step
    double oldt = _stage(1);

    int n = 2;
    while (true) {
      
      _log.fine("Step $n");
      
      // improve integral with a larger number of steps
      final double t = _stage(n);
      _log.info("Calculated value $t");

      // estimate error
      final double delta = (t - oldt).abs();
      final double limit =
          max(getAbsoluteAccuracy(),
              getRelativeAccuracy() * (oldt.abs() + t.abs()) * 0.5);

      // check convergence
      if ((iterations.count + 1 >= getMinimalIterationCount()) && (delta <= limit)) {
        return t;
      }

      // prepare next iteration
      double ratio = min(4.0, pow(delta / limit, 0.5 / _abscissas.length));
      n = max((ratio * n).truncate(), n + 1);
      oldt = t;
      iterations.incrementCount();
      
    } 
    
  }
    
 
  /**
   * Compute the n-th stage integral.
   * @param n number of steps
   * @return the value of n-th stage integral
   * @throws TooManyEvaluationsException if the maximum number of evaluations
   * is exceeded.
   */
   double _stage(final int n) {

    // set up the step for the current stage
    final double step     = (this.max - this.min) / n;
    final double halfStep = step / 2.0;

    // integrate over all elementary steps
    double midPoint = this.min + halfStep;
    double sum = 0.0;
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < _abscissas.length; ++j) {
        sum += _weights[j] * computeObjectiveValue(midPoint + halfStep * _abscissas[j]);
      }
      midPoint += step;
    }

    return halfStep * sum;
  }

  
    
  
